import React, { Component } from 'react';
import TOC from './components/TOC.js'
import './App.css';

class Subject1 extends Component { //props 예제
  render() { //컴퍼넌트는 반드시 하나의 최상위 태그로 시작해야함 
    return (
      <header>
        <h1>{this.props.title}</h1>
        {this.props.sub}
    </header>
    );
  }
}
class Subject2  extends Component { //state 예제
  render() { 
    return (
      <header>
        <h1><a href="/" onClick={function(e){
          e.preventDefault();
          this.props.onChangePage();
        }.bind(this)}>{this.props.title}</a></h1>
        {this.props.sub}
    </header>
    );
  }
}
class App extends Component {
  constructor(props){ //초기화 담당
    super(props);
    this.state = {
      mode:"sss",
      selected_content_id : 2,
      main:{title:"Welcome", sub:"Hello React!"},
      subject2:{title:"WEB", sub:"subject2"},
      contents:[
        {id:1, title:'HTML', desc:'HTML is for information'},
        {id:2, title:'CSS', desc:'CSS is for design'},
        {id:3, title:'JavaScript', desc:'JavaScript is for interactive'}
      ]
    }
  }
  render() {
    var _title, _sub = null;
    if (this.state.mode === "main") {
      _title = this.state.main.title;
      _sub = this.state.main.sub;
    } else if(this.state.mode === "read") {
      _title = this.state.contents[this.state.selected_content_id-1].title;
      _sub = this.state.contents[this.state.selected_content_id-1].desc;
    }
    return (
      <div className="App">
        <header> 
        <h1><a href="/" onClick={function(e){
          e.preventDefault(); //이게 없으면 클릭할 때 마다 페이지가 reload되는데. reload없이 동적인 페이지를 만들 수 있는 것이 react의 장점이기 때문에, 의마가 없어진다. 
                              // 해당 함수를 호출한 tag의 기본 동작을 못하게 하는 함수이다. 
          alert("event raised"); 
          this.setState({
            mode : "main"
          });
        }.bind(this)}>{_title}</a></h1>
        {_sub}
      </header>
      {/* <Subject1 title="WEB" sub="welcome!"></Subject1> */}
      {/* <Subject2
        title = {this.state.subject2.title}
        sub = {this.state.subject2.sub}
        onChangePage = {function(){ // 우리가 만든 이벤트 
          this.setState({mode:"main"})
        }.bind(this)}>

      </Subject2> */}
      <TOC 
        onChangePage = {function(){
          alert('hihihihi');
          this.setState({
            mode:"read" 
            })

        }.bind(this)}
        data={this.state.contents}
      ></TOC>
      </div>
    );
  }
}

export default App;
